import React,{useState} from 'react';
import {db} from '../firebase';

const Upload_meat = () => {
const [price, setPrice] =useState()
const [gram, setGram] =use

  function PushData() {
    db.add({ // dbが送信先 送信データはオブジェクトの形
        price: price,
    });
  }
  return(
    <div>
    <input onChange={(e)=>{
      setPrice(e.target.value)
    }}></input>
    <button onClick={()=>{
      PushData()
    }} >出　品</button>
  </div>
  );
};
export default Upload_meat
