import React,{useState} from 'react';
import {db} from '../firebase';

const Login_farmersmeat = () => {
const [price, setPrice] =useState()

  function PushData() {
    db.add({ // dbが送信先 送信データはオブジェクトの形
        price: price,
    });
}
  return(
    <div>
    <input onChange={(e)=>{
      setPrice(e.target.value)
    }}></input>
    <button onClick={()=>{
      PushData()
    }} >出　品</button>
  </div>
  );
};
export default Login_farmersmeat
