const [price, setPrice] =useState()
const [gram, setGram] =useState()

  function PushData() {
    db.add({ // dbが送信先 送信データはオブジェクトの形
        price: price,
    });
  }
  return(
    <div>
      <p>価格</p>
      <input onChange={(e)=>{
        setPrice(e.target.value)
      }}></input>
      <input onChange={(e)=>{
        setPrice(e.target.value)
      }}></input>
      <button onClick={()=>{
        PushData()
      }} >出　品</button>
    </div>
  );
};
